function myFunction(){document.body.scrollTop>sticky||document.documentElement.scrollTop>sticky?(navbarLogo.src=navbar.dataset.logoDark,navbar.classList.remove("navbar-invisible"),navbar.classList.add("navbar-visible")):(navbarLogo.src=navbar.dataset.logoLight,navbar.classList.remove("navbar-visible"),navbar.classList.add("navbar-invisible"))}if("/"==window.location.pathname||"/BVBD-Middleman/"==window.location.pathname){window.onscroll=function(){myFunction()};var banner=document.querySelector(".banner"),navbar=document.querySelector(".navbar-wagon"),navbarLogo=document.querySelector("#navbar-logo"),sticky=banner.offsetHeight-50}